sqlMap.select:
/* Pr01.selectPr01List */ SELECT A.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY NO_NTC_PLTE_SRAL DESC) AS RNUM , A.NO_NTC_PLTE_SRAL AS noNtcPlteSral , A.CL_NTC_PLTE AS clNtcPlte , A.NO_ATCH_FILE_THUM_SRAL AS noAtchFileThumSral , A.SBJT_NTC_PLTE AS sbjtNtcPlte , A.CNTS_SMRY AS cntsSmry , A.SBJT_NTC_PLTE_EN AS sbjtNtcPlteEn , A.CNTS_SMRY_EN AS cntsSmryEn , A.DT_RGST AS dtRgst , CASE WHEN A.CL_GBN = '01' THEN '공지사항' WHEN A.CL_GBN = '02' THEN '프로그램/행사' WHEN A.CL_GBN = '03' THEN '수상' ELSE A.CL_GBN END clGbnNm , B.NM_PHYS_FILE AS nmPhysFile , B.CNTS_FILE_DTIL AS cntsFileDtil FROM HABT_NTC_PLTE A LEFT JOIN HABT_FILE_LST_INFO B ON A.NO_ATCH_FILE_THUM_SRAL = B.NO_ATCH_FILE_SRAL WHERE 1=1 AND CL_KO_ENG = 'K' /* 국문영문 구분 ( K : 국문, E : 영문 ) */ AND YN_NTC = 'Y' /* 게시여부 ( Y : 게시함, N : 게시안함 ) */ AND YN_ELMN = 'N' /* 삭제여부 ( Y : 삭제함, N : 삭제안함 ) */
AND (SBJT_NTC_PLTE LIKE '%' || #searchCnts# || '%' OR CNTS_SMRY LIKE '%' || #searchCnts# || '%')
AND (SBJT_NTC_PLTE LIKE '%' || #searchCnts# || '%')
AND (CNTS_SMRY LIKE '%' || #searchCnts# || '%')
) A WHERE RNUM BETWEEN #sNum# AND #eNum#
